import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  miFormulario:FormGroup = this.fb.group({
    email:['usuario-test1@gmail.com',[Validators.required,Validators.email]],
    password:['12345678',[Validators.required,Validators.minLength(6)]]
  })

  constructor(private fb:FormBuilder,
              private router:Router,
              private authService:AuthService){}

  login(){
    const {email, password} = this.miFormulario.value;
            
    this.authService.login(email,password)
      .subscribe(valid => {
        if(valid === true){
          
          this.router.navigateByUrl('/dashboard/classroom')
        
        }else{
          Swal.fire({
            icon:'error',
            title:'Credenciales invalidas',
            showConfirmButton:true,
            confirmButtonColor: '#18619b',
            
          })
        }
    });
    
  }
  
  campoNoValido(campo:string) {
    return this.miFormulario.get(campo)?.touched &&
    this.miFormulario.get(campo)?.invalid;
  }

}
<div class="container-sm d-flex flex-column">
    <img src="assets/img/Logo.png" class="img-fluid mx-auto" alt="logo" width="230px">
    <div class="container-sm mt-5">
        <form autocomplete="off"
              [formGroup]="miFormulario"
              (ngSubmit)="login">
            
            <div class="mb-3 lnombre">
              <label for="exampleInputEmail1" class="form-label text-primary">Email</label>
              <input type="email" 
              class="form-control bg-transparent py-3 ps-3" 
              formControlName="email" 
              style="border-color: #18619B;" 
              placeholder="Correo electronico">
              <small *ngIf="campoNoValido('email')" class="form-text text-danger">
                valide su email
              </small>
            </div>
            
            
            <div class="mb-3 lnombre">
              <label for="exampleInputPassword1" class="form-label text-primary">Contraseña</label>
              <input type="password" 
              class="form-control bg-transparent py-3 ps-3" 
              formControlName="password" 
              style="border-color: #18619B;">
              <small *ngIf="campoNoValido('password')" class="form-text text-danger">
                La contraseña es obligatoria
              </small>
            </div>
            
            
            <div class="mb-3 form-check">
              <input type="checkbox" class="form-check-input" id="exampleCheck1">
              <label class="form-check-label" for="exampleCheck1" style="color: #18619B;">Recuerdame</label>
            </div>
            
            <div class="text-center container-sm" >
            <button type="button"
                    class="btn btn-transparent center-block" 
                    style="border: none; color: #18619B;">¿Olvidaste la contraseña?</button>
            </div>
            
            <div class="text-center container-sm mt-3">
                <button type="submit" 
                class="btn btn-primary"
                (click)="login()"
                [disabled]="miFormulario.invalid"  
                style=" background-color: #18619B; width: 100%;" >Ingresar</button>
            </div>
                
            <div class="text-center container-sm mt-3 " >
                    <a routerLink="/auth/register" type="button" class="btn btn-primary" style=" background-color: #ffff; color: #18619B; width: 100%;" >Crear una Cuenta</a>
            </div>
          </form>
    </div>

</div>
